<template>

	<view class="fuiAudio-wrap" @tap="handleControl">
		<view class="fuiAudio-left">
			<image src="data:image/png;base64," class="fuiAudio-icon" :class="{'fuiAudio-hidden':isPlay}" />
			<image src="data:image/gif;base64," class="fuiAudio-icon" :class="{'fuiAudio-hidden':!isPlay}" />
		</view>
		<view class="fuiAudio-right">
			<view class="fuiAudio-title" v-if="title">{{ title }}</view>
			<view class="fuiAudio-author" v-if="desc">{{ desc }}</view>
			<view class="fuiAudio-progress">
				<view class="fuiAudio-current" :style="{width:`${progress}%`}"></view>
			</view>
			<view class="fuiAudio-time">
				<text class="fuiAudio-begin">{{ currentTimeStr }}</text>
				<text class="fuiAudio-total">{{ durationStr }}</text>
			</view>
		</view>
	</view>
</template>

<script>
	export default {
		name: "firstui-audio",
		props: {
			title: {
				type: String,
				default: ''
			},
			desc: {
				type: String,
				default: ''
			},
			src: {
				type: String,
				default: ''
			}
		},
		watch: {
			src(newValue, oldValue) {
				if (newVal) {
					this.handleInitAudio(newVal)
				}
			}
		},
		data() {
			return {
				isPlay: false,
				currentTimeStr: '00:00',
				durationStr: '00:00',
				progress: 0
			};
		},
		created() {
			this.innerAudioContext = null;
			this.src && this.handleInitAudio(this.src)
		},
		// #ifndef VUE3
		beforeDestroy() {
			this.innerAudioContext && this.innerAudioContext.destroy()
		},
		// #endif
		// #ifdef VUE3
		beforeUnmount() {
			this.innerAudioContext && this.innerAudioContext.destroy()
		},
		// #endif
		methods: {
			handleInitAudio(src) {
				this.innerAudioContext = uni.createInnerAudioContext()
				this.innerAudioContext.src = src
				this.innerAudioContext.onPlay(() => {
					console.log('开始播放')
				})

				this.innerAudioContext.onCanplay(() => {
					// 这是一个迷，据说要手动先触发这个属性，后面才能用setTimeout获取真实时长
					const _fui_duration = this.innerAudioContext.duration;
					setTimeout(() => {
						const durationStr = this.parseTime(this.innerAudioContext.duration)
						this.durationStr = durationStr;
					}, 1000)
				})

				this.innerAudioContext.onError((res) => {
					console.log(res.errMsg)
					console.log(res.errCode)
				})

				this.innerAudioContext.onEnded(() => {
					this.isPlay = false;
				})

				this.innerAudioContext.onTimeUpdate(() => {
					const currentTime = this.innerAudioContext.currentTime
					const duration = this.innerAudioContext.duration
					const currentTimeStr = this.parseTime(currentTime)
					const progress = (currentTime / duration) * 100
					this.currentTimeStr = currentTimeStr;
					this.progress = progress;
				})
			},

			handleControl() {
				if (!this.isPlay) {
					this.isPlay = true;
					this.innerAudioContext && this.innerAudioContext.play()
				} else {
					this.isPlay = false;
					this.innerAudioContext && this.innerAudioContext.pause()
				}
			},

			parseTime(time) {
				const minute = Math.floor(time / 60)
				const second = Math.floor(time % 60)
				return `${minute < 10 ? '0' + minute : minute}:${second < 10 ? '0' + second : second}`
			}
		}
	}
</script>

